mapper.select:
SELECT 

FROM biz_book a

WHERE a.id = #{id}

SELECT 

FROM biz_book a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.price BETWEEN #{beginPrice} AND #{endPrice}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM biz_book a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE biz_book SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
